  const ImageScreen = ({ route, navigation }) => {
    const { index } = route.params;
    const [scale, setScale] = useState(1);
    const [baseScale, setBaseScale] = useState(1);
    const swiperRef = useRef(null);
  
    const onPinchGestureEvent = ({ nativeEvent }) => {
      const newScale = baseScale * nativeEvent.scale;
      if (newScale < 1) {
        setScale(1);
      } else {
        setScale(newScale);
      }
    };
  
    const onPinchHandlerStateChange = ({ nativeEvent }) => {
      if (nativeEvent.oldState === 4) {
        setBaseScale(scale);
      }
    };
  
    const resetScale = () => {
      setScale(1);
      setBaseScale(1);
    };
  
    return (
      <View style={styles.container}>
        <View style={styles.header}>
          <TouchableOpacity style={styles.closeButton} onPress={() => navigation.goBack()}>
            <Icon name="times" size={25} color="#fff" />
          </TouchableOpacity>
          <Text style={styles.title}>{data[index].title}</Text>
        </View>
        <PinchGestureHandler onGestureEvent={onPinchGestureEvent} onHandlerStateChange={onPinchHandlerStateChange}>
          <View style={styles.imageContainer}>
            <Swiper
              ref={swiperRef}
              showsPagination
              loop={false}
              index={index}
              onIndexChanged={resetScale}
              containerStyle={styles.swiperContainer}
            >
              {data.map((item, index) => (
                <View style={styles.slide} key={index}>
                  <Image
                    source={item.image}
                    style={{ ...styles.image, width: scale * width, height: scale * height }}
                    resizeMode="cover"
                  />
                </View>
              ))}
            </Swiper>
            <View style={styles.bottom}>
              <Text style={styles.bottomText}>Swipe down to close</Text>
            </View>
          </View>
        </PinchGestureHandler>
      </View>
    );
  };
  


